INSERT INTO `cache_filter` VALUES ('filtered_html::689573b92f283e39be4f3d3a4ffbc7e88b08e32930771465ad1c9c26e3558f5b','<p>Leader Colleges are demonstrating the power of the Achieving the Dream Student-Centered Model of Institutional Improvement. They show us it is possible to raise persistence and graduation rates, close achievement gaps, and change lives. Leader Colleges embody the ultimate goal of Achieving the Dream, and as such, serve as mentors within our community of learners.</p><p>To be eligible for Leader College distinction, colleges must show three or more years of improvement on one or more of these five measures:</p><ul><li>Course completion</li><li>Advancement from developmental to credit-bearing courses</li><li>Completion of college-level math and English courses</li><li>Term-to-term and year-to-year retention</li><li>Completion of certificates or degrees</li></ul>',0,1351802827,0);
