INSERT INTO `cache_filter` VALUES ('filtered_html::ce86aa5118c558a845430e713310825d4a323b0b44850bc7501291cce4908aaf','<p>Leader Colleges have demonstrated commitment to and made progress on the four principles of Achieving the Dream:</p><ul><li>committed leadership</li><li>use of evidence to improve programs and services</li><li>broad engagement, and</li><li>systemic institutional improvement.</li></ul><p>They have also shown three years of sustained student success improvement. We expect these colleges to serve as mentors within the Achieving the Dream community of learners, as well as advocates for the principles of Achieving the Dream.</p><p><a href=\"http://www.achievingthedream.org/get_involved/leader_colleges\">Learn more</a>.</p>',0,1351810976,0);
