INSERT INTO `field_data_body` VALUES ('node','news',0,1662,3489,'und',0,'<table border=\"0\" cellpadding=\"2\" cellspacing=\"7\" style=\"vertical-align:top;\"><tr><td width=\"80\" align=\"center\" valign=\"top\"><font style=\"font-size:85%;font-family:arial,sans-serif\"></font></td><td valign=\"top\" class=\"j\"><font style=\"font-size:85%;font-family:arial,sans-serif\"><br /><div style=\"padding-top:0.8em;\"><img alt=\"\" height=\"1\" width=\"1\" /></div><div class=\"lh\"><a href=\"http://news.google.com/news/url?sa=t&amp;fd=R&amp;usg=AFQjCNGb7B4ueO8Mkexj0IiYjIBeen-OKw&amp;url=http://www.tjcnewspaper.com/figs-makes-the-transition-into-college-smoother-1.2795270\"><b>FIGs makes the transition into college smoother</b></a><br /><font size=\"-1\"><b><font color=\"#6f6f6f\">Junior College</font></b></font><br /><font size=\"-1\">It is also part of TJC&#39;s &quot;<b>Achieving The Dream</b>&quot; initiative designed to help students succeed. FIGS participants enroll in linked courses, study together, and share declared majors while living in a designated residential hall.</font><br /><font size=\"-1\" class=\"p\"></font><br /><font class=\"p\" size=\"-1\"><a class=\"p\" href=\"http://news.google.com/news/more?pz=1&amp;ned=us&amp;ncl=d4xV9qy0Eltcl7M&amp;region\"><nobr><b></b></nobr></a></font></div></font></td></tr></table>',NULL,'filtered_html');
