INSERT INTO `field_data_body` VALUES ('node','news',0,2855,5193,'und',0,'<table border=\"0\" cellpadding=\"2\" cellspacing=\"7\" style=\"vertical-align:top;\"><tr><td width=\"80\" align=\"center\" valign=\"top\"><font style=\"font-size:85%;font-family:arial,sans-serif\"></font></td><td valign=\"top\" class=\"j\"><font style=\"font-size:85%;font-family:arial,sans-serif\"><br /><div style=\"padding-top:0.8em;\"><img alt=\"\" height=\"1\" width=\"1\" /></div><div class=\"lh\"><a href=\"http://news.google.com/news/url?sa=t&amp;fd=R&amp;usg=AFQjCNFvvxI5Wt9RCFpHYVToW7NAK5OHKw&amp;url=http://www.marketwatch.com/story/pennsylvanias-community-colleges-poised-to-meet-challenges-set-forth-in-report-2012-04-24\"><b>Pennsylvania&#39;s Community Colleges Poised to Meet Challenges Set Forth in Report</b></a><br /><font size=\"-1\"><b><font color=\"#6f6f6f\">MarketWatch (press release)</font></b></font><br /><font size=\"-1\">Seven of Pennsylvania&#39;s community colleges are named &quot;Leader Colleges&quot; in the national <b>Achieving the Dream</b> Initiative, a effort focused on closing achievement gaps and accelerating student success nationwide. To be eligible for Leader College <b>...</b></font><br /><font size=\"-1\" class=\"p\"></font><br /><font class=\"p\" size=\"-1\"><a class=\"p\" href=\"http://news.google.com/news/more?pz=1&amp;ned=us&amp;ncl=ddjX1cvLVMSdANM&amp;region\"><nobr><b>and more&nbsp;&raquo;</b></nobr></a></font></div></font></td></tr></table>',NULL,'filtered_html');
